mapper.select:
SELECT 

FROM application_templates


        AND category = #{param.category}


        AND (
            name LIKE CONCAT('%', #{param.search}, '%')
            OR description LIKE CONCAT('%', #{param.search}, '%')
        )


ORDER BY ${param.sortBy} ${param.sortDirection}
LIMIT #{param.size} OFFSET #{param.offset}

SELECT COUNT(*) FROM application_templates
AND category = #{param.category}
AND ( name LIKE CONCAT('%', #{param.search}, '%') OR description LIKE CONCAT('%', #{param.search}, '%') )

mapper.insert:
INSERT INTO application_templates (
    id, name, category, version, description, 
    icon_url, template, sort_weight
) VALUES (
    #{id}, #{name}, #{category}, #{version}, #{description},
    #{iconUrl}, #{template}, #{sortWeight}
)
